
public class AulaRepeticaoExercicio2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// while, do while, for
		
		int valorMax = 50;
		
		for(int i=0; i <= valorMax; i++) {
			if(i > 25) {
				break;
			}
			System.out.println(i);

		}
		
		
	}

}
